import ReactDOM from 'react-dom';
import './App.css';
import React, { useState } from 'react';

function App() {
  let [count, setCount] = useState(0);
  function like()
  {
    setCount(count++);
  }
    return (
      <div>
      <div id="con"><h1 id="h"> like <span id = "ap2">{count}</span></h1></div>
      <div id="con"><h1 id="h"> dislike <span id = "ap2">{count}</span></h1></div>
      <div id="bt">
      <button id = "btn1" onClick = {like}> ++ </button>
      <button id = "btn2" onClick = {()=>{setCount(count--)}}> -- </button>
      </div>
      </div>
    );
}

export default App;
